@section('script')

<script type="text/javascript">
	$('document').ready(function(){
        // table ketersedian logistik
        let table2 = $('#table-ketersediaan-logistik').DataTable();

        function format_logistik(data) {
        return  "<table class='table mt-md-0'>"+
                "<tr>"+
                    "<td width='9%'></td>"+
                    "<td width='50%'>Sepatu boot</td>"+
                    "<td>"+ data.sepatu_booth +"</td>"+
                "</tr>"+
                "<tr>"+
                    "<td></td>"+
                    "<td>Goggle</td>"+
                    "<td>"+ data.google +"</td>"+
                "</tr>"+
                "<tr>"+
                    "<td></td>"+
                    "<td>Baju</td>"+
                    "<td>"+ data.baju +"</td>"+
                "</tr>"+
                "<tr>"+
                    "<td></td>"+
                    "<td>Masker N95</td>"+
                    "<td>"+ data.masker_95 +"</td>"+
                "</tr>"+
                "<tr>"+
                    "<td></td>"+
                    "<td>Masker bedah</td>"+
                    "<td>"+ data.masker_bedah +"</td>"+
                "</tr>"+
            "</table>"
        }

        $('#table-ketersediaan-logistik tbody').on('click', 'td button.tombol-detail-logistik', function () {
            let valueData = JSON.parse($(this).val());
            // console.log(valueData)
            var tr = $(this).closest('tr');
            var row = table2.row( tr );

            if ( row.child.isShown() ) {
                // This row is already open - close it
                row.child.hide();
                tr.removeClass('shown');
            }
            else {
                // Open this row
                row.child(format_logistik(valueData)).show();
                tr.addClass('shown');
            }
            // toggle button
            $(this).toggleClass('btn-outline-primary btn-primary')
            $(this).find('i').toggleClass('fa-chevron-up fa-chevron-down')
        } );
    })
</script>
@endsection